css:
.textcenter{
    text-align: center;
  }
  .borderclass{
    border-bottom: 1px solid dodgerblue;
  }
  table{
    width: 100%;
    text-align: left;
    border-collapse: collapse;
  }
  table,td,th{
    border: 1px solid dodgerblue;
    font-size: 11px;
  }
  .print-format{
    padding: 12px !important;
  }
  table.table1{
      margin-top:2px;
      page-break-inside:auto;
  }
  table.table1 thead{
      display:table-header-group;
  }

html:
{% set warehouse %}{% endset %}
{% set fpo_link %}{% endset %}
{% set cc_name %}{% endset %}
{% if doc.set_warehouse %}
  {% set warehouse = frappe.get_doc("Warehouse",doc.set_warehouse) %}
    {% if warehouse %}
      {% if warehouse.fpo_link %}
        {% set fpo_link = frappe.get_doc("S4S FPO Link",warehouse.fpo_link) %}
      {% endif %}
      {% if warehouse.cc_link %}
        {% set cc_name = frappe.get_doc("Warehouse",warehouse.cc_link) %}
      {% endif %}
    {% endif %}
{% endif %}
{% set links = frappe.get_all('Dynamic Link', filters={'link_doctype': 'Warehouse', 'link_name': doc.set_warehouse, 'parenttype': 'Address'}, fields=['parent']) %}
{% if links %}
    {% set addr = frappe.get_doc("Address", links[0].parent) %}
{% endif %}

{% set link = frappe.get_all('Dynamic Link', filters={'link_doctype': 'Supplier', 'link_name': doc.supplier, 'parenttype': 'Address'}, fields=['parent']) %}
{% if link %}
    {% set addr_supp = frappe.get_doc("Address", link[0].parent) %}
{% endif %}

{% set cc_add %}{% endset %}
  {% if warehouse.cc_address %}
   {% set cc_add = frappe.get_doc("Address", warehouse.cc_address) %}
  {% endif %}

{% set supplier %}{% endset %}
{% if doc.supplier %}
  {% set supplier = frappe.get_doc("Supplier",doc.supplier) %}
{% endif %}

{% set s4s_supplier %}{% endset %}
{% if doc.supplier %}
  {% set s4s_supplier = frappe.get_doc("S4S Supplier",{"supplier":doc.supplier}) %}
{% endif %}

<table border="1px" width="100%">
  <tr>
    <td width="35%">
      {% if letter_head and not no_letterhead -%}
        <div class="letter-head">{{ letter_head }}</div>
      {% endif %}
    </td>
    <td width="65%">
     {% if fpo_link %}
        <br>
        <div style="font-size: 17px;text-align:center;"><b>{{fpo_link.name}}</b></div><br>
         <div style="text-align:center;">
        {{fpo_link.address}}<br><br>
        Mob No :- {{fpo_link.mobile_number}}
        </div>
      {% endif %}<br><br>

     {% if doc.set_warehouse %}
      {% if doc.set_warehouse == warehouse.cc_link %}
        <div style="font-size:12px;text-align:center;"><b>CC DETAILS:</b></div>
          <div style="text-align:center;">
              {% set a = cc_name.parent_warehouse %}
              {% set listx =  a.split('-') %}
              {{listx[0]}}<br>
              {% if cc_add %}
                  {{cc_add.address_line1}},<br>
                  {{cc_add.city}},{{cc_add.state}},<br>
                  {{cc_add.country}}
              {% endif %}
          </div>
      {% else %}
       <div style="font-size:12px;text-align:center;"><b>CC DETAILS:</b></div>
          <div style="text-align:center;">
              {% set a = cc_name.parent_warehouse %}
              {% set listx =  a.split('-') %}
              {{listx[0]}}<br>
              {% if cc_add %}
                  {{cc_add.address_line1}},<br>
                  {{cc_add.city}},{{cc_add.state}},<br>
                  {{cc_add.country}}
              {% endif %}
          </div>
          <br>
       <div style="font-size:12px;text-align:center;"><b>VLCC DETAILS:</b></div>
          <div style="text-align:center;">
            {% set a = warehouse.parent_warehouse %}
            {% set listx =  a.split('-') %}
            {{listx[0]}}<br>
            {% if addr %}
                {{addr.address_line1}},{{addr.city}},<br>
                {{addr.county}},{{addr.state}},<br>
                {{addr.country}}
            {% endif %}
          </div>
      {% endif %}
     {% endif %} 
    </td>
  </tr>
  <tr>
    <td colspan="2" style="text-align:center;font-size: 18px;"><b>Farmer Receipt</b></td>
  </tr>
</table>
<br>
<table border="1px" width="100%">
  <tr>
    <td width="50%">
      <div class="row">
        <div class="col-xs-12">
          <div class="col-xs-4"><b>Farmer Name :</b></div>
          <div class="col-xs-8"><b>{{ doc.supplier_name }}</b></div>
        </div>
      </div>
      <div class="row">
        <div class="col-xs-12">
           <div class="col-xs-4"><b>Address :</b><br><b>Village :</b><br><b>Taluka : </b><br><b>District : </b></div>
           <br>
          <div class="col-xs-8"  style="text-align:left;">
             {% if addr_supp %}
                  {{addr_supp.address_line1}}<br>
                  {{addr_supp.city}}<br>
                  {{addr_supp.county}}
             {% endif %}
          </div>
        </div>
      </div>
      <br>
      {% if supplier %}
        {% if supplier.mobile_no_1 %}
          <div class="row">
            <div class="col-xs-12">
              <div class="col-xs-4"><b>Mobile No. :</b></div>
              <div class="col-xs-8"><b>{{ supplier.mobile_no_1 }}</b></div>
            </div>
          </div>
        {% endif %}
      {% endif %}
    </td>
    <td width="50%">
      <div class="row">
        <div class="col-xs-12">
          <div class="col-xs-6"><b>Receipt No :</b></div>
          <div class="col-xs-6"><b>{{ doc.name }}</b></div>
        </div>
      </div>
      <br>
      <div class="row">
        <div class="col-xs-12">
          <div class="col-xs-6"><b>Date :</b></div>
          <div class="col-xs-6"><b>{{doc.get_formatted("transaction_date")}}</b></div>
        </div>
      </div>
      <br>
      <div class="row">
        <div class="col-xs-12">
          <div class="col-xs-6"><b>Inward Location Code :</b></div>
          <div class="col-xs-6">
            {% if doc.set_warehouse %}  
                {% set a = warehouse.parent_warehouse %}
                {% set listx =  a.split('-') %}
                <b>{{listx[0]}}</b></div>
            {% endif %}
        </div>
      </div>
    </td>
  </tr>
</table>
<br>
<table border="1px" width="100%">
  <tr>
    <th style="text-align:center;width:30%;">Product Name</th>
    <th style="text-align:center;width:12.5%;">Weight(in kgs)</th>
    <th style="text-align:center;width:12.5%;">Rate(in INR)</th>
    <th style="text-align:center;width:35%;">Amount</th>
  </tr>
  <tr>
    <td style="text-align:center;">{% if doc.purchase_receipt_details[0] %}{{doc.purchase_receipt_details[0].item_name}}{% endif %}</td>
    <td style="text-align:center;">{{doc.weight_after_kadta}}</td>
    <td style="text-align:center;">{{"%.2f"|format(doc.total_amount/doc.weight_after_kadta)}}</td>
    <td style="text-align:right;">{{doc.get_formatted("total_amount")}}</td>
  </tr>
  <tr>
    <td colspan="3">
      {% if s4s_supplier %}
        <div class="row">
          <div class="col-xs-12">
            <div class="col-xs-4"><b>Account Holder Name</b></div>
            <div class="col-xs-8">: <b>{{ s4s_supplier.account_holder_name }}</b></div>
          </div>
        </div>
        <div class="row">
          <div class="col-xs-12">
            <div class="col-xs-4"><b>Bank Name</b></div>
            <div class="col-xs-8">: <b>{{ s4s_supplier.bank_name }}</b></div>
          </div>
        </div>
        <div class="row">
          <div class="col-xs-12">
            <div class="col-xs-4"><b>Account No.</b></div>
            <div class="col-xs-8">: <b>{{ s4s_supplier.account_number }}</b></div>
          </div>
        </div>
        <div class="row">
          <div class="col-xs-12">
            <div class="col-xs-4"><b>IFSC Code</b></div>
            <div class="col-xs-8">: <b>{{ s4s_supplier.ifsc_code }}</b></div>
          </div>
        </div>
        <div class="row">
          <div class="col-xs-12">
            <div class="col-xs-4"><b>Branch</b></div>
            <div class="col-xs-8">: <b>{{ s4s_supplier.branch }}</b></div>
          </div>
        </div>
      {% endif %}
    </td>
    <td colspan="2" style="padding: 0px 0px 0px 0px !important;">
      <table width="100%" style="margin: 0px 0px 0px 0px !important;">
        {% for row in doc.taxes %}
          <tr>
            <td width="50%">
              {{row.description}}
            </td>
            <td style="text-align:right;width:50%;">
              {{row.get_formatted("tax_amount")}}
            </td>
          </tr>
        {% endfor %}
        <tr>
          <td><b>Grand Total</b></td>
          <td style="text-align:right;width:50%;">
            {{doc.get_formatted("grand_total")}}
          </td>
        </tr>
      </table>
    </td>
  </tr>
</table>
<br>
<table border="1px" width="100%">
  <tr>
    <td width="50%" style="text-align:center;">
      <br><br><br><br><br>
      <b>Farmers Sign</b>
    </td>
    <td width="50%" style="text-align:center;">
      <br><br><br><br><br>
      <b>Supervisor Sign</b>
    </td>
  </tr>
</table>
